.loader {
  width: 150px;
  height: 150px;
  margin: 0 auto;
  position: relative;
  -webkit-animation: loader 2s infinite ease-in-out;
          animation: loader 2s infinite ease-in-out; }

.loader span {
  width: 50px;
  height: 50px;
  position: absolute;
  left: 0;
  top: 0;
  border: 4px solid #0B1B48; }

.loader span:nth-child(1) {
  -webkit-animation: span-1 2s ease-in-out infinite;
          animation: span-1 2s ease-in-out infinite; }

.loader span:nth-child(2) {
  -webkit-animation: span-2 2s ease-in-out infinite;
          animation: span-2 2s ease-in-out infinite; }

.loader span:nth-child(3) {
  -webkit-animation: span-3 2s ease-in-out infinite;
          animation: span-3 2s ease-in-out infinite; }

.loader span:nth-child(4) {
  -webkit-animation: span-4 2s ease-in-out infinite;
          animation: span-4 2s ease-in-out infinite; }

.slider {
  max-width: 900px;
  height: 450px;
  margin: 20px auto;
  position: relative; }

.slide1, .slide2, .slide3, .slide4 {
  position: absolute;
  width: 100%;
  height: 100%; }

.slide1 {
  background: url(c99a96affa0f391c4f09cce07b3f661b.jpg) no-repeat center;
  background-size: cover;
  animation: fade 11s infinite;
  -webkit-animation: fade 11s infinite; }

.slide2 {
  background: url(d48a5452a02f0c3e3f5e36d658e2fd84.jpg) no-repeat center;
  background-size: cover;
  animation: fade2 11s infinite;
  -webkit-animation: fade2 11s infinite; }

.slide3 {
  background: url(65974ff24fd02526ff998ee106d0e916.jpg) no-repeat center;
  background-size: cover;
  animation: fade3 11s infinite;
  -webkit-animation: fade3 11s infinite; }

.slide4 {
  background: url(3fca54858e9b82790a54a022cff22afb.jpg) no-repeat center;
  background-size: cover;
  animation: fade4 11s infinite;
  -webkit-animation: fade4 11s infinite; }

@-webkit-keyframes fade {
  0% {
    opacity: 1; }
  33.333% {
    opacity: 0; }
  66.666% {
    opacity: 1; }
  100% {
    opacity: 1; } }

